from tkinter import *root = Tk()root.geometry("500x500")root.title("Driving license")label_heading = Label(root , text = "Spaceship License" , bg = "red" , width = 499 , height = 3 , font = ("arial" , 20))label_id = Label(root , text = "ID: Beluga#0001" , font = ("Helvetica" , 20 , "bold"))label_name = Label(root , text = "Name: Scrat" , font = ("Helvetica" , 20 , "bold"))label_age = Label(root , text = "DOB : 5000 BCE" , font = ("Helvetica" , 20 , "bold"))label_pin = Label(root , text = "PIN: 0010101010101010101010101010" , font = ("Helvetica" , 20 , "bold"))label_address = Label(root , text = "Home: Planet ursurus B 1909" , font = ("Helvetica" , 20 , "bold"))label_auth = Label(root , text = "Authorized: v500 , v9000 engine" , font = ("Helvetica" , 20 , "bold"))def ShowLicense():    label_heading.pack()    label_id.pack()    label_name.pack()    label_age.pack()    label_pin.pack()    label_address.pack()    label_auth.pack()    showbutton = Button(root , text = "Show License" , command = ShowLicense)showbutton.pack()root.mainloop()